package br.vandersoncamp.eplants.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import javax.xml.bind.annotation.XmlRootElement;import java.io.Serializable;import java.util.Date;@Entity@XmlRootElement@Table(name = "plantas")@SequenceGenerator(name = "seq_plantas", sequenceName = "seq_plantas", initialValue = 1, allocationSize = 1)public class Planta implements Serializable, EntityID {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_plantas")    private Long id;    @NotNull    @ManyToOne    @JoinColumn(name = "id_planta_familia", nullable = false)    private PlantaFamilia familia;    @ManyToOne    @JoinColumn(name = "id_planta_utilizacao")    private PlantaUtilizacao utilizacao;    @NotNull    @Column(name = "nome_comum", length = 255, nullable = false)    private String nomeComun;    @NotNull    @Column(name = "nome_cientifico", length = 255, nullable = false)    private String nomeCientifico;    @NotNull    @Temporal(TemporalType.TIMESTAMP)    @Column(name = "dt_coleta", nullable = false)    private Date dataColeta;    @NotNull    @Size(max = 255)    @Column(name = "local_coleta", length = 255, nullable = false)    private String localColeta;    @Size(max = 500)    @Column(name = "observacao", length = 500, nullable = true)    private String observacao;    public Planta() {        super();    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public PlantaFamilia getFamilia() {        return familia;    }    public void setFamilia(PlantaFamilia familia) {        this.familia = familia;    }    public PlantaUtilizacao getUtilizacao() {        return utilizacao;    }    public void setUtilizacao(PlantaUtilizacao utilizacao) {        this.utilizacao = utilizacao;    }    public String getNomeComun() {        return nomeComun;    }    public void setNomeComun(String nomeComun) {        this.nomeComun = nomeComun;    }    public String getNomeCientifico() {        return nomeCientifico;    }    public void setNomeCientifico(String nomeCientifico) {        this.nomeCientifico = nomeCientifico;    }    public Date getDataColeta() {        return dataColeta;    }    public void setDataColeta(Date dataColeta) {        this.dataColeta = dataColeta;    }    public String getLocalColeta() {        return localColeta;    }    public void setLocalColeta(String localColeta) {        this.localColeta = localColeta;    }    public String getObservacao() {        return observacao;    }    public void setObservacao(String observacao) {        this.observacao = observacao;    }}