package br.vandersoncamp.eplants.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.xml.bind.annotation.XmlRootElement;import java.io.Serializable;@Entity@XmlRootElement@Table(name = "usuarios")@SequenceGenerator(name = "seq_usuarios", sequenceName = "seq_usuarios", initialValue = 1, allocationSize = 1)public class Usuario implements Serializable, EntityID {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_usuarios")    private Long id;    @Column(name = "documento", nullable = false)    private String documento;    @NotNull    @Column(name = "nome", length = 255, nullable = false)    private String nome;    @Column(name = "email", length = 255)    private String email;    @Column(name = "telefone")    private Long telefone;    public Usuario() {        super();    }    @Override    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    public String getDocumento() {        return documento;    }    public void setDocumento(String documento) {        this.documento = documento;    }    public Long getTelefone() {        return telefone;    }    public void setTelefone(Long telefone) {        this.telefone = telefone;    }}