package br.vandersoncamp.resources;import br.vandersoncamp.eplants.data.PlantaFamiliaBusiness;import br.vandersoncamp.eplants.model.PlantaFamilia;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/plantasfamilia")@RequestScopedpublic class PlantaFamiliaResource {    @Inject    private PlantaFamiliaBusiness business;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        PlantaFamilia familia = business.buscar(id);        if (familia == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(familia).build();    }    @GET    @Path("all")    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(business.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(PlantaFamilia familia) {        business.criar(familia);        return Response.status(Response.Status.CREATED).entity(familia).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(PlantaFamilia familia, @PathParam("id") Long id) {        if (!id.equals(familia.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        business.atualizar(familia);        return Response.status(Response.Status.OK).entity(familia).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        business.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}