package br.vandersoncamp.resources;import br.vandersoncamp.eplants.data.PlantaBusiness;import br.vandersoncamp.eplants.model.Planta;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.*;import java.util.List;@Path("/plantas")@RequestScopedpublic class PlantaResource {    @Inject    private PlantaBusiness business;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        Planta planta = business.buscar(id);        if (planta == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(planta).build();    }    @GET    @Path("all")    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(business.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(Planta planta) {        business.criar(planta);        return Response.status(Response.Status.CREATED).entity(planta).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(Planta planta, @PathParam("id") Long id) {        if (!id.equals(planta.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        business.atualizar(planta);        return Response.status(Response.Status.OK).entity(planta).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        business.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}