package br.vandersoncamp.resources;import br.vandersoncamp.eplants.data.PlantaFamiliaBusiness;import br.vandersoncamp.eplants.data.PlantaUtilizacaoBusiness;import br.vandersoncamp.eplants.model.PlantaFamilia;import br.vandersoncamp.eplants.model.PlantaUtilizacao;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.*;@Path("/plantasutilizacao")@RequestScopedpublic class PlantaUtilizacaoResource {    @Inject    private PlantaUtilizacaoBusiness business;    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        PlantaUtilizacao utilizacao = business.buscar(id);        if (utilizacao == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(utilizacao).build();    }    @GET    @Path("all")    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(            @QueryParam("filterField") String filterField,            @QueryParam("filterValue") String filterValue,            @QueryParam("order") String order) {        return Response.ok(business.pesquisar(filterField, filterValue, order)).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(PlantaUtilizacao utilizacao) {        business.criar(utilizacao);        return Response.status(Response.Status.CREATED).entity(utilizacao).build();    }    @PUT    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(PlantaUtilizacao utilizacao, @PathParam("id") Long id) {        if (!id.equals(utilizacao.getId())) {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere do ID da URL").build();        }        business.atualizar(utilizacao);        return Response.status(Response.Status.OK).entity(utilizacao).build();    }    @DELETE    @Path("{id}")    public Response excluir(@PathParam("id") Long id) {        business.excluir(id);        return Response.status(Response.Status.NO_CONTENT).build();    }}