package br.vandersoncamp.eplants.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import javax.xml.bind.annotation.XmlRootElement;import java.io.Serializable;@Entity@XmlRootElement@Table(name = "plantas_familia")@SequenceGenerator(name = "seq_plantas_familia", sequenceName = "seq_plantas_familia", initialValue = 1, allocationSize = 1)public class PlantaFamilia implements Serializable {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_plantas_familia")    private Long id;    @NotNull    @Size(max = 255)    @Column(name = "descricao", nullable = false)    private String descricao;    public PlantaFamilia() {        super();    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getDescricao() {        return descricao;    }    public void setDescricao(String descricao) {        this.descricao = descricao;    }}