package br.vandersoncamp.eplants.model;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import javax.xml.bind.annotation.XmlRootElement;import java.io.Serializable;@Entity@XmlRootElement@Table(name = "usuarios")@SequenceGenerator(name = "seq_usuarios", sequenceName = "seq_usuarios", initialValue = 1, allocationSize = 1)public class Usuario implements Serializable {    private static final long serialVersionUID = 1L;    @Id    @GeneratedValue(strategy = GenerationType.SEQUENCE, generator = "seq_usuarios")    private Long id;    @NotNull    @Size(max = 255)    @Column(name = "nome", nullable = false)    private String nome;    @Size(max = 255)    @Column(name = "email")    private String email;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }}