package br.vandersoncamp.eplants.model;import javax.persistence.Id;import javax.validation.constraints.NotNull;import javax.validation.constraints.Pattern;import javax.validation.constraints.Size;import java.util.Objects;public class Usuario {    private Long id;    @NotNull    @Size(min = 1, max = 255)    private String nome;    @Size(min = 1, max = 255)    private String email;    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getNome() {        return nome;    }    public void setNome(String nome) {        this.nome = nome;    }    public String getEmail() {        return email;    }    public void setEmail(String email) {        this.email = email;    }    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Usuario usuario = (Usuario) o;        return Objects.equals(id, usuario.id) &&                Objects.equals(nome, usuario.nome) &&                Objects.equals(email, usuario.email);    }    @Override    public int hashCode() {        return Objects.hash(id, nome, email);    }    @Override    public String toString() {        return "Usuario{" +                "id=" + id +                ", nome='" + nome + '\'' +                ", email='" + email + '\'' +                '}';    }}