package br.vandersoncamp.eplants.service;import javax.inject.Inject;import javax.persistence.EntityManager;import javax.validation.Valid;import java.util.List;public abstract class AbstractService<T, I> {    private Class<T> entityClass;    @Inject    private EntityManager em;    protected AbstractService() {    }    public AbstractService(Class<T> entityClass) {        this.entityClass = entityClass;    }    public T gravar(@Valid T entity) {        em.persist(entity);        return entity;    }    public T atualizar(@Valid T entity) {        em.merge(entity);        return entity;    }    public void excluir(I id) {        T entity = buscar(id);        em.remove(entity);    }    public List<T> pesuisar() {        return em.createQuery("FROM " + entityClass.getName()).getResultList();    }    public T buscar(I id) {        return em.find(entityClass, id);    }}