package br.vandersoncamp.eplants.service;import javax.inject.Inject;import javax.persistence.EntityManager;import javax.persistence.Query;import java.util.List;public abstract class AbstractService<T> {    private Class<T> entityClass;    @Inject    private EntityManager em;    public AbstractService(Class<T> entityClass) {        this.entityClass = entityClass;    }    public void save(T entity) {        em.persist(entity);    }    public void remove(T entity) {        em.remove(entity);    }    public List<T> findAll() {        Query query = em.createQuery("from " + entityClass.getName());        return query.getResultList();    }    public T findById(Long id) {        return (T) em.find(entityClass.getClass(), id);    }}