package br.vandersoncamp.eplants.service;import br.vandersoncamp.eplants.model.Usuario;import javax.enterprise.context.ApplicationScoped;import java.util.ArrayList;import java.util.List;@ApplicationScopedpublic class UsuarioService {    private List<Usuario> data = new ArrayList<>();    private long nextId = 0;    public List<Usuario> pesquisar() {        return data;    }    public Usuario buscar(long id) {        for (Usuario usuario : data) {            if (usuario.getId().equals(id))                return usuario;        }        return null;    }    public void criar(Usuario usuario) {        usuario.setId(++nextId);        data.add(usuario);    }    public void atualizar(long id, Usuario usuario) {        for (int i = 0; i < data.size(); i++) {            if (data.get(i).getId().equals(id)) {                data.set(i, usuario);                return;            }        }    }    public void excluir(long id) {        Usuario usuario = null;        for (int i = 0; i < data.size(); i++) {            if (data.get(i).getId().equals(id)) {                usuario = data.get(i);                break;            }        }        if (usuario != null)            data.remove(usuario);    }}