package br.vandersoncamp.eplants.rest;import br.vandersoncamp.eplants.model.Usuario;import br.vandersoncamp.eplants.service.UsuarioService;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("usuarios")public class UsuarioResource {    @Inject    private UsuarioService service;    @GET    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar() {        return Response.ok(service.pesquisar()).build();    }    @GET    @Path("{id}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        Usuario usuario = service.buscar(id);        if (usuario == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(usuario).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(Usuario usuario) {        service.criar(usuario);        return Response.status(Response.Status.CREATED).entity(usuario).build();    }}