package br.vandersoncamp.resources;import br.vandersoncamp.eplants.data.PlantaBusiness;import br.vandersoncamp.eplants.model.Planta;import br.vandersoncamp.eplants.model.PlantaUtilizacao;import javax.enterprise.context.RequestScoped;import javax.inject.Inject;import javax.ws.rs.*;import javax.ws.rs.core.MediaType;import javax.ws.rs.core.Response;@Path("/plantas")@RequestScopedpublic class PlantaResource {    @Inject    private PlantaBusiness business;    @GET    @Path("{id:[0-9][0-9]*}")    @Produces(MediaType.APPLICATION_JSON)    public Response buscar(@PathParam("id") Long id) {        Planta planta = business.buscar(id);        if (planta == null) {            return Response.status(Response.Status.NOT_FOUND).build();        }        return Response.ok(planta).build();    }    @GET    @Produces(MediaType.APPLICATION_JSON)    public Response pesquisar(@DefaultValue("0") @QueryParam("offset") Integer offset,                              @DefaultValue("50") @QueryParam("limit") int limit,                              @QueryParam("sort") String sort) {        return Response.ok(business.pesquisar()).build();    }    @POST    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response criar(Planta planta) {        business.criar(planta);        return Response.status(Response.Status.CREATED).entity(planta).build();    }    @PUT    @Path("{id:[0-9][0-9]*}")    @Produces(MediaType.APPLICATION_JSON)    @Consumes(MediaType.APPLICATION_JSON)    public Response atualizar(Planta planta, @PathParam("id") Long id) {        if (planta.getId().equals(id)) {            business.atualizar(planta);            return Response.status(Response.Status.CREATED).entity(planta).build();        } else {            return Response.status(Response.Status.BAD_REQUEST).entity("ID do objeto difere da URL").build();        }    }    @DELETE    @Path("{id:[0-9][0-9]*}")    public Response excluir(@PathParam("id") Long id) {        business.excluir(id);        return Response.ok().build();    }}